import { useEffect, useRef } from 'react';
const usePreviousValue = (newValue) => {
    const previousValueRef = useRef(newValue);
    useEffect(() => {
        previousValueRef.current = newValue;
    }, [newValue]);
    return previousValueRef.current;
};
export { usePreviousValue };
