import React from 'react';
import ReactDOM from 'react-dom';
import { ThemeProvider, createTheme, withStyles, Arwes, Image } from 'arwes';

const styles = theme => ({
  root: {
    padding: [theme.padding, 0],
    background: theme.background.primary.level0
  },
  title: {
    textDecoration: 'underline'
  }
});


const MyHeader = withStyles(styles)(({ classes, children }) => (
  <header className={classes.root}>
    <h1 className={classes.title}>{children}</h1>
  </header>
));

const App = () => (
  <ThemeProvider theme={createTheme()}>
    <Arwes>
    <div style={{ margin: '0 auto', padding: 20, maxWidth: 600 }}>
            <p>
            Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod
            tempor incididunt ut labore et dolore magna aliqua.
            </p>
            <Image animate resources='https://version1-breakpoint1.arwes.dev/static/img/wallpaper.jpg'>
                The vast universe around us
            </Image>
            <p>
            Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod
            tempor incididunt ut labore et dolore magna aliqua.
            </p>
        </div>
    </Arwes>
  </ThemeProvider>
);


ReactDOM.render(<App />, document.querySelector('#root'));
